<template>	<div class="window" :style="frameStyles"	>		<span class="resize-line-horizontal"></span>		<span class="resize-line-vertical"></span>		<div class="caption-bar">			<button class="system-button">				<span></span>			</button>			<span class="caption-text">				{{ caption }} KOE			</span>			<button class="control-button">				<span class="icon minimize"></span>			</button>			<button class="control-button">				<span class="icon maximize"></span>			</button>		</div>		<div class="client">			<slot></slot>		</div>	</div></template><script lang="ts">	import Component from "vue-class-component";	import {WindowMode} from "@/components/kernel/ui/Windows/WindowProperties";	import Frame from "@/components/kernel/ui/Base/Frame.vue";	@Component(	{		data()		{			return {			}		}	})	export default class Window extends Frame	{		/**		 * Style of the frame		 */		public style: WindowMode = WindowMode.Normal;		protected updateWindowStyle()		{			console.log('Switching to window style ' + this.style);			switch(this.style)			{				case WindowMode.Normal:					this.setFramePosition(this.frameSize.left, this.frameSize.top);					this.setFrameSize(this.frameSize.width, this.frameSize.height);					break;				case WindowMode.Maximized:					this.setFramePosition(0, 0);					this.setFrameSize(window.innerWidth, window.innerHeight);					break;				case WindowMode.Minimized:					break;				case WindowMode.Hidden:					break;			}		}		public set windowStyle(value: WindowMode)		{			if(this.style == WindowMode.Normal)				this.retainCurrentFrame();			this.style = value;			this.updateWindowStyle();		}		public get windowStyle()		{			return this.style;		}	}</script>